mapper.select:
SELECT ENTREZ_GENE_ID AS entrezGeneId, HUGO_GENE_SYMBOL AS hugoGeneSymbol, COUNT(*) AS numberOfAlterations, COUNT(DISTINCT(SAMPLE_ID)) AS numberOfAlteredCases FROM (
SELECT MUTATION.SAMPLE_ID, mutation.ENTREZ_GENE_ID, gene.HUGO_GENE_SYMBOL, mutation.GENETIC_PROFILE_ID, mutation_event.MUTATION_TYPE FROM MUTATION_EVENT INNER JOIN mutation ON MUTATION_EVENT.MUTATION_EVENT_ID = mutation.MUTATION_EVENT_ID INNER JOIN gene ON MUTATION_EVENT.ENTREZ_GENE_ID = gene.ENTREZ_GENE_ID
LOWER(mutation_event.MUTATION_TYPE) IN
LOWER(#{type})
UNION
SELECT sample_cna_event.SAMPLE_ID, cna_event.ENTREZ_GENE_ID, gene.HUGO_GENE_SYMBOL, sample_cna_event.GENETIC_PROFILE_ID, CAST(CNA_EVENT.ALTERATION AS VARCHAR) FROM cna_event INNER JOIN sample_cna_event ON cna_event.CNA_EVENT_ID = sample_cna_event.CNA_EVENT_ID INNER JOIN gene ON cna_event.ENTREZ_GENE_ID = gene.ENTREZ_GENE_ID
cna_event.ALTERATION IN
#{type}
) as JoinedTable
GROUP BY ENTREZ_GENE_ID;

SELECT ENTREZ_GENE_ID AS entrezGeneId, HUGO_GENE_SYMBOL AS hugoGeneSymbol, COUNT(*) AS numberOfAlterations, COUNT(DISTINCT(PATIENT_ID)) AS numberOfAlteredCases FROM (
SELECT patient.STABLE_ID as PATIENT_ID, mutation.ENTREZ_GENE_ID, gene.HUGO_GENE_SYMBOL, mutation_event.MUTATION_TYPE as alteration, genetic_profile.STABLE_ID as GENETIC_PROFILE_STABLE_ID FROM mutation_event INNER JOIN mutation ON mutation_event.MUTATION_EVENT_ID = mutation.MUTATION_EVENT_ID INNER JOIN gene ON mutation_event.ENTREZ_GENE_ID = gene.ENTREZ_GENE_ID INNER JOIN sample_profile ON sample_profile.SAMPLE_ID = mutation.SAMPLE_ID AND sample_profile.GENETIC_PROFILE_ID = mutation.GENETIC_PROFILE_ID INNER JOIN genetic_profile on sample_profile.GENETIC_PROFILE_ID = genetic_profile.GENETIC_PROFILE_ID INNER JOIN sample ON sample_profile.SAMPLE_ID = sample.INTERNAL_ID INNER JOIN patient ON sample.PATIENT_ID = patient.INTERNAL_ID
LOWER(mutation_event.MUTATION_TYPE) IN
LOWER(#{type})
UNION
SELECT patient.STABLE_ID as PATIENT_ID, cna_event.ENTREZ_GENE_ID , gene.HUGO_GENE_SYMBOL, CAST(CNA_EVENT.ALTERATION AS VARCHAR), genetic_profile.STABLE_ID as GENETIC_PROFILE_STABLE_ID FROM cna_event INNER JOIN sample_cna_event ON cna_event.CNA_EVENT_ID = sample_cna_event.CNA_EVENT_ID INNER JOIN gene ON cna_event.ENTREZ_GENE_ID = gene.ENTREZ_GENE_ID INNER JOIN genetic_profile on SAMPLE_CNA_EVENT.GENETIC_PROFILE_ID = genetic_profile.GENETIC_PROFILE_ID INNER JOIN sample ON SAMPLE_CNA_EVENT.SAMPLE_ID = sample.INTERNAL_ID INNER JOIN patient ON sample.PATIENT_ID = patient.INTERNAL_ID
cna_event.ALTERATION IN
#{type}
) as JoinedTable
GROUP BY ENTREZ_GENE_ID;

